import React from 'react'
import Wrapper from './style'

//components
import PressStart from './press_start'
import Menu from './menu'
//items
import Logo from '../../design/icons/liftquest'

class StartMenu extends React.Component {
  state = {
    enter_pressed: false,
  }
  enter = () =>
    this.setState({enter_pressed: true})
  check_for_enter = e => {
    console.log('bitch')
  }
  render = () =>
    <Wrapper className='start-menu' onKeyUp={this.check_for_enter}>
      <div className='logo'>
        <Logo />
        <h1>LIFT QUEST</h1>
      </div>
      {this.state.enter_pressed
        ? <Menu />
        : <PressStart />
      }
    </Wrapper>
}

export default StartMenu
