import React from 'react'
// import {Route as R} from 'react-router-dom'
import Wrapper from './style'

//components
import Menu from './menu'
//items
import Logo from '../../design/icons/liftquest'

class StartMenu extends React.Component {
  state = {
    active: '/',
    options: [{route: '/start', text: 'press enter button'}]
  }
  componentDidMount = () => {
    window.addEventListener('keyup', this.check_key)
  }
  componentWillUnmount = () => {
    window.removeEventListener('keyup', this.check_key)
  }
  start = () => {
    switch(this.state.active) {
      case '/start': {
        this.setState({
          active: 'continue',
          options: [
            {route: '/login', text: 'continue'},
            {route: '/register', text: 'new game'},
            {route: '/', text: 'about'}
          ]
        })
        break
      }
      case 'continue': {
        this.props.history.pushState('/login')
        break
      }
      default: console.log('default')
    }
  }
  change_option = e => {
    this.setState({
      active: e.currentTarget.name
    })
  }
  update_active = inc => {
    let idx = this.state.options
    // let idx = this.state.options.findIndex(option => options.route === this.state.active)
    console.log(idx)
    const max = this.state.options.length
    if(inc) {
      idx += inc
      if(idx > max - 1) idx = 0
      if(idx < 0) idx = max -1
      console.log(idx)
      console.log(this.state.options[idx])
      this.setState({
        active: this.state.options[idx].route
      })
    }
  }
  check_key = e => {
    switch(e.key) {
      case 'Enter': this.start(); break;
      case 'ArrowDown': this.update_active(1); break;
      case 'ArrowUp': this.update_active(-1); break;
      default: console.log('nope')
    }
  }
  render = () =>
    <Wrapper className='start-menu'>
      <div className='logo'>
        <Logo />
        <h1>LIFT QUEST</h1>
      </div>
      <Menu data={this.state} click={this.check_for_enter} change={this.change_option}/>
    </Wrapper>
}

export default StartMenu
