import React from 'react'
import Wrapper from './style'

//components
import PressStart from './press_start'
import Menu from './menu'
//items
import Logo from '../../design/icons/liftquest'

class StartMenu extends React.Component {
  state = {
    enter_pressed: false,
  }
  componentDidMount = () => {
    window.addEventListener('keyup', this.check_for_enter)
  }
  componentWillUnmount = () => {
    window.removeEventListener('keyup', this.check_for_enter)
  }
  enter = () => {
    console.log('bitch!')
    this.setState({enter_pressed: true})
  }
  check_for_enter = e => {
    if(e.key === 'Enter') this.enter()
  }
  render = () =>
    <Wrapper className='start-menu'>
      <div className='logo'>
        <Logo />
        <h1>LIFT QUEST</h1>
      </div>
      {this.state.enter_pressed
        ? <Menu />
        : <PressStart click={this.click}/>
      }
    </Wrapper>
}

export default StartMenu
