import React from 'react'
import Wrapper from './style'

//components
import Menu from './menu'
//items
import Logo from '../../design/icons/liftquest'

class StartMenu extends React.Component {
  state = {
    active: 'press enter button',
    options: ['press enter button']
  }
  componentDidMount = () => {
    window.addEventListener('keyup', this.check_for_enter)
  }
  componentWillUnmount = () => {
    window.removeEventListener('keyup', this.check_for_enter)
  }
  start = () => {
    this.setState({
      active: 'continue',
      options: ['continue', 'new game', 'about'],
    })
  }
  change_option = e => {
    console.log(e.currentTarget.innerText)
    this.setState({
      active: e.currentTarget.innerText
    })
  }
  check_for_enter = e => {
    if(e.key === 'Enter') this.start()
  }
  render = () =>
    <Wrapper className='start-menu'>
      <div className='logo'>
        <Logo />
        <h1>LIFT QUEST</h1>
      </div>
      <Menu data={this.state} click={this.check_for_enter} change={this.change_option}/>
    </Wrapper>
}

export default StartMenu
